INSERT INTO `brands` VALUES ('1795', '2004.12.23', '2004201202962', 'A23L1/06(2006.01)，A23L3/00(2006.01)，A23L3/36(2006.01)', null, 'Special shaped cup jelly automatic sterilizing and cooling production line', '异形杯果冻自动消毒、冷却生产线', '2006.01.25', 'utility model', '实用新型', null, null, null, '肖一九', null, null, '本实用新型公开了一种异形杯果冻自动消毒、冷却生产线，沿果冻输送方向依次包括输入线、消毒机、冷却机和输出线，还包括盛装果冻箱的吊篮和控制系统，其中输入线与消毒机之间、消毒机与冷却机之间、冷却机与输出线之间均设置有传输吊篮的提升移行机，控制系统对消毒机和冷却机内的温度、时间参数进行调节。本实用新型在采用了一系列的机械式自动化转运方式后，在很大程度上减少了人工操作的介入，工作效率大幅提高，在整个生产过程中果冻不会碰撞、挤压，内容物可均匀散开逐渐冷却成形，产品质量得到了进一步提升，同时封闭的结构及保温层使综合能源消耗下降20％左右。', null, '2017-04-26 14:21:09', '537');
