INSERT INTO `brands` VALUES ('1798', '2008.06.27', '2008100291694', 'B03B7/00(2006.01)I，B03C1/10(2006.01)I，B03B5/62(2006.01)I，B03B5/48(2006.01)I，B03B11/00(2006.01)I', null, 'Spiral type foreign body separator', '一种螺旋式异物分离机', '2012.05.09', 'Invention authorization', '发明授权', null, null, null, '肖一九', null, null, '本发明公开了一种螺旋式异物分离机。它包括有入料口、出料口、电机、循环水系统和机架，在入料口和出料口处分别设置有第一接水盘和第二接水盘，两接水盘垂直分布，入料口和第一接水盘之间设置有接盘导向槽，两接水盘之间通过装有筛选机构的螺旋槽连通，螺旋槽和第一接水盘均固定在中心轴上，利用带座轴承把中心轴固定在机架上，依靠皮带轮、皮带和电机带动中心轴旋转，饮料泵通过输水管将水输送到入料口及螺旋槽处，而且入料口、螺旋槽及第二接水盘还连通有将水回流到水箱的回流水管。本发明的有益效果在于，该机能有效去除原物料中夹杂的各种异物，人工介入少、生产效率高、产品质量好，水源利用率高，可以为各个筛选机构的提供循环用水。', null, '2017-04-26 14:21:09', '537');
