INSERT INTO `brands` VALUES ('1803', '2013-05-27', '2013102017854', 'B03B7/00(2006.01)I', null, 'Wide wave type foreign body separator', '宽幅波浪式异物分离机', '2013-09-25', 'Invention announcement', '发明公布', null, null, null, '李永军', null, null, '本发明公开了宽幅波浪式异物分离机，该异物分离机包括筛选单元、水循环单元和机架，机架固定连接筛选单元和水循环单元，筛选单元包括由入料口处依次连接的第一分离槽、第一过滤网、第二分离槽和第二过滤网，第一过滤网倾斜设置在第一分离槽和第二分离槽之间、并平滑连接第一分离槽尾端和第二分离槽首端，第二过滤网倾斜设置在第二分离槽尾端、并平滑连接第二分离槽尾端。本发明公开的宽幅波浪式异物分离机通过设置两级分离槽和相应过滤网，并使它们平滑连接，使得果肉在第一、第二分离槽的尾端随瀑布式水流流下，异物自然被截留到过滤筛网上，该异物分离机结构紧凑、进料口高度低、便于操作。', null, '2017-04-26 14:21:09', '537');
