INSERT INTO `brands` VALUES ('1806', '2013.03.20', '2013100900956', 'B65B61/20(2006.01)I', null, 'Single feeding device for straw', '一种吸管单根投放装置', '2013.06.12', 'Invention announcement', '发明公布', null, null, null, '李永军', null, null, '本发明公开了一种吸管单根投放装置，包括一料仓，所述料仓的底部与一转鼓连接，所述转鼓的外表面设有若干与吸管配合的第一凹槽，该转鼓通过一滑道与一传送带的一端衔接，所述传送带的另一端与一投放轮衔接，所述投放轮上设有若干与吸管配合的第二凹槽，所述投放轮的下方设有一与投放轮衔接的滑槽，所述滑槽上端的一侧设有若干凸起。本发明通过转鼓、滑道、传送带、投放轮和滑槽的相互配合，可将杂乱的吸管单根分离并整齐成列排放后逐一单根投放，本发明结构简单，能耗小，工作效率高。', null, '2017-04-26 14:21:09', '537');
