INSERT INTO `brands` VALUES ('1809', '2004.10.12', '2004201185276', 'B65B5/10', null, 'Jelly automatic packaging line system', '果冻自动包装线系统', '2005.11.30', 'utility model', '实用新型', null, null, null, '肖一九', null, null, '本实用新型公开了一种果冻自动包装线系统，包括果冻输送装置、装箱前纸箱处理装置、多头称、喷码机、气动推箱机构、装箱后纸箱处理装置、无动力滚筒输送机及控制系统，控制系统分别与果冻输送装置、装箱前纸箱处理装置、人工添加果冻装置、果冻提升装置及多头称、喷码机、气动推箱机构、装箱后纸箱处理装置、无动力滚筒输送机相连接，果冻输送装置下游依次连接多头称、喷码机、装箱后纸箱处理装置、无动力滚筒输送机，所述多头称一侧设置装箱前纸箱处理装置，装箱前纸箱处理装置经气动推箱机构连接装箱后纸箱处理装置。采用上述自动包装线系统后，包装时占用空间减少，避免了中转搬运及重复作业，并且不会重复占用空间，工作效率大大提高。', null, '2017-04-26 14:21:09', '537');
