INSERT INTO `brands` VALUES ('1835', '2013.08.09­', '201320485564X', 'E06B9/68', null, 'Fireproof rolling door machine device with plug', '带有插头的防火卷门机装置', '2014.02.12', 'utility model', '实用新型', null, null, null, '罗光明;张艳军;陈以平', null, null, '一种带有插头的防火卷门机装置，它包括插头Ⅰ、控制线Ⅰ、卷门机、插头Ⅱ、控制线Ⅱ和卷门机控制器,其结构特点是控制线Ⅰ的一端连接在卷门机的接线端上，另一端连接有插头Ⅰ，控制线Ⅱ的两端分别连接插头Ⅱ和卷门机控制器，所述插头Ⅰ和插头Ⅱ配套；本实用新型通过设计的插头使得卷门机和卷门机控制器可以单独安装，避免了安装时控制线的拉扯，方便了安装和调试，提高了防火卷门机装置整体的控制质量和卷门机的工作效果。', null, '2017-04-26 15:16:06', '539');
