INSERT INTO `media_records` VALUES ('2675', null, '2017-03-29 00:03:00', null, '潭区、泸县等国家知识产权强县工程试点县（区），泸州高新区、四川长江经济开发区等知识产权试点园区，四川化工职业学院、泸州职业技术学院等高校，泸州老窖股份有限公司、四川科瑞德制药有限公司等知识产权试点示范企业，实地了解了政府、园区、高校、企业在实施创新驱动发展和知识产权战略，推动知识产权试点城市建设中取得的成效和遇到的问题，听取了有关单位的意见', 'http://lz.newssc.org/system/20170329/002144001.html', '四川新闻网泸州频道', '泸州市人大调研我市国家知识产权试点城市建设工作', null, '2017-04-24 11:28:11', '467', 'Luzhou Municipal People\'s Congress of China\'s national intellectual property pilot cities');
