INSERT INTO `rulingdocuments` VALUES ('669', null, null, '2017-04-24 20:00:48', null, null, null, '分期付款买卖合同纠纷', null, '', null, '', null, null, null, 'c2015rongminchu563_d52_frongjiang@AVuf0mvKmaKpj2IKRkxf', '黔东南林源汽车销售有限公司榕江分公司,黔东南林源汽车销售有限公司,上海汽车贸易有限公司,上海大众汽车有限公司', '', '（2015）榕民初字第563号', '(Fig 2015) minchuzi No. 563rd', '贵州省榕江县人民法院', 'Rongjiang County People\'s Court of Guizhou', null, '一、撤销原告张家平与被告黔东南林源汽车销售有限公司榕江分公司在2015年3月28日签订的《定车协议》。二、原告张家平于本判决生效之日起十日内将其所购的大众途观1.8T自动两驱舒适版黑色机动车（车厂牌型号：大众汽车牌SVM6451SED，车辆识别号／车架号为：LSVXL65N1D2196910）退还给被告黔东南林源汽车销售有限公司榕江分公司。三、被告黔东南林源汽车销售有限公司于本判决生效之日起十日内一次性退还原告张家平购车款人民币212800元。四、被告黔东南林源汽车销售有限公司于本判决生效之日起十日内一次性赔偿原告张家平车辆购置税20300元、保险费7462.62元、汽车修理费4386元、汽车装饰费10440元，共计人民币42588.62元。五、被告黔东南林源汽车销售有限公司于本判决生效之日起十日内赔偿原告张家平三倍购车款人民币638400元。六、驳回原告张家平要求被告黔东南林源汽车销售有限公司赔偿按揭贷款手续费用14331.6元的诉讼请求。如果未按本判决指定的期间履行给付金钱义务，应当依据《中华人民共和国民事诉讼法》第二百二十九条之规定，加倍支付迟延履行的债务利息。案件受理费12872元，由被告黔东南林源汽车销售有限公司负担。如不服本判决，可在接到判决之日起十五日内，向本院递交上诉状，并按对方当事人的人数提出副本，上诉于贵州省黔东南苗族侗族自治州中级人民法院。', null, '', '张家平', 'Peoples Insurance Company of China Taizhou branch', '1457452800000', '张家平诉林源汽车榕江分公司等买卖合同纠纷案一审民事判决书', '(2015) taking No. No. 00336 taikai', null, null, '505');
