INSERT INTO `rulingdocuments` VALUES ('692', null, null, '2017-04-25 13:00:26', null, null, null, '相邻关系纠纷', null, '', null, '', null, null, null, 'c2016321111minchu627@AVujd8Wn8F1AEkkla32O', '袁孝如,深圳市大众物业管理有限公司,深圳市大众物业管理有限公司镇江分公司', 'Xiao Ru yuan', '（2016）苏1111民初627号', '(2016) No. 1111 of the Soviet Union in the early Republic of China No. 627', '江苏省镇江市润州区人民法院', 'Runzhou District People\'s Court of Jiangsu city of Zhenjiang Province', null, '一、被告袁孝如于本判决生效之日起十日内赔偿原告陈钧、王丽的各项损失合计10951.76元。二、驳回原告陈钧、王丽对深圳市大众物业管理有限公司、深圳市大众物业管理有限公司镇江分公司的诉讼请求。如果未按本判决指定的期间履行给付金钱义务，应当依照《中华人民共和国民事诉讼法》第二百五十三条之规定，加倍支付迟延履行期间的债务利息。案件受理费减半收取为40元、鉴定费5000元，合计5040元，由被告袁孝如负担。如不服本判决，可在判决书送达之日起十五日内，向本院递交上诉状，并按对方当事人的人数提出副本，上诉于江苏省镇江市中级人民法院。（附上诉须知）', null, '', '陈钧,王丽', 'Philip Chan', '1482076800000', '陈钧、王丽与袁孝如、深圳市大众物业管理有限公司等相邻关系纠纷一审民事判决书', 'Chen Jun, Wang Li and, the Shenzhen Public Property Management Co., Ltd. and other adjacent relations of the first instance civil judgment', null, null, '523');
INSERT INTO `rulingdocuments` VALUES ('693', null, null, '2017-04-25 13:00:26', null, null, null, '小额借款合同纠纷', null, '', null, '', null, null, null, 'c2016321111minchu2034@AVujd8Wn8F1AEkkla32O', '靖江市润元农村小额贷款有限公司', 'Shenzhen Dazhong Property Management Co., Ltd.', '（2016）苏1111民初2034号', '(2016) No. 1111 of the Soviet Union in the early Republic of China No. 2034', '江苏省镇江市润州区人民法院', 'Runzhou District People\'s Court of Jiangsu city of Zhenjiang Province', null, '被告靖江市润元农村小额贷款有限公司于本判决生效之日起十日内支付原告深圳市大众物业管理有限公司镇江分公司物业费21742元。如果未按本判决指定的期间履行给付金钱义务，应当依照《中华人民共和国民事诉讼法》第二百五十三条之规定，加倍支付迟延履行期间的债务利息。案件受理费652元，由原告深圳市大众物业管理有限公司镇江分公司负担130元，由被告靖江市润元农村小额贷款有限公司负担522元。此款已由原告全部预交本院，故被告应将所承担的诉讼费连同上述还款一并支付给原告。如不服本判决，可在判决书送达之日起十五日内，向本院递交上诉状及副本一份，上诉于江苏省镇江市中级人民法院。（附上诉须知）', null, '', '深圳市大众物业管理有限公司镇江分公司', 'Wang', '1481558400000', '深圳市大众物业管理有限公司镇江分公司与靖江市润元农村小额贷款有限公司小额借款合同纠纷、物业服务合同纠纷一审民事判决书', 'Shenzhen Public Property Management Co., Ltd. Zhenjiang branch and the Jingjiang rural small loan Co., Ltd. small loan contract disputes, property services contract dispute first instance civil judgment', null, null, '523');
