INSERT INTO `rulingdocuments` VALUES ('743', null, null, '2017-04-26 14:18:32', null, null, null, '小额借款合同纠纷', null, '', null, '', null, null, null, 'c2017320612minchu380@AVuo5bkpmaKpj2IKVJ55', '邱训明,张云,杨煜宁,王春子', 'Qiuxunming', '（2017）苏0612民初380号', '(2017) No. 0612 of the Soviet Union in the early Republic of China No. 380', '江苏省南通市通州区人民法院', 'Tongzhou District Nantong people\'s court, Jiangsu', null, '一、被告邱训明、张云于本判决发生法律效力后十日内偿还原告南通市通州区恒晟农村小额贷款股份有限公司借款本金500000元及利息、逾期利息（利息自2015年12月11日起按月利率15‰计算至2016年5月20日，逾期利息自2016年5月21日至实际还款之日按月利率15‰上浮20%计算）；二、被告邱训明、张云于本判决发生法律效力后十日内偿付原告南通市通州区恒晟农村小额贷款股份有限公司律师费5000元；三、被告杨煜宁、王春子对上述第一、二项判决确定的被告邱训明、张云的给付义务承担连带责任；被告如果未按本判决指定的期限履行给付义务，应当依照《中华人民共和国民事诉讼法》第二百五十三条之规定，加倍支付迟延履行期间的债务利息。案件受理费减半收取4425元、保全费3770元，合计8195元，由四被告（四被告负担部分，原告已代垫，待执行时由四被告一并给付原告）。如不服本判决，可在判决书送达之日起十五日内，向本院递交上诉状，并按对方当事人的人数提出副本，上诉于江苏省南通市中级人民法院，同时按不服判决的数额向该院预交上诉案件受理费（户名：南通市财政局，开户行：中国银行南通市西被闸支行，账号：47&times;&times;&times;82）。', null, '', '南通市通州区恒晟农村小额贷款股份有限公司', 'Tongzhou District Nantong Heng Sheng rural microfinance Limited by Share Ltd', '1487606400000', '南通市通州区恒晟农村小额贷款股份有限公司与邱训明、张云等小额借款合同纠纷一审民事判决书', 'Tongzhou District Heng Sheng rural microfinance Limited by Share Ltd and Qiu Xunming, Zhang Yun and other small loan contract dispute first instance civil verdict', null, null, '536');
