-- Table structure for groups
-- ----------------------------
DROP TABLE IF EXISTS `groups`;
CREATE TABLE `groups` (
  `groupId` int(11) NOT NULL AUTO_INCREMENT,
  `cpmCustomerId` int(11) DEFAULT NULL,
  `delFlag` varchar(255) DEFAULT NULL,
  `email` varchar(255) DEFAULT NULL,
  `expeditedPrice` int(11) DEFAULT NULL,
  `groupName` varchar(255) DEFAULT NULL,
  `lanEmail` int(11) DEFAULT NULL,
  `monitorBasePrice` double DEFAULT NULL,
  `monitorDiscount` double DEFAULT NULL,
  `note` varchar(255) DEFAULT NULL,
  `photoPath` varchar(255) DEFAULT NULL,
  `point` int(11) DEFAULT NULL,
  `regDate` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`groupId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

