DROP TABLE IF EXISTS `message`;
CREATE TABLE `message` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '默认id',
  `url_id` varchar(100) DEFAULT NULL COMMENT 'urlid',
  `content` varchar(400) DEFAULT NULL COMMENT '内容',
  `from_id` int(20) DEFAULT NULL COMMENT '发送Id',
  `to_id` int(20) DEFAULT NULL COMMENT '接收Id',
  `is_read` varchar(20) DEFAULT NULL COMMENT '是否已读',
  `is_delete` varchar(20) DEFAULT NULL COMMENT '是否删除',
  `send_time` datetime DEFAULT NULL COMMENT '发送时间',
  `receive_time` datetime DEFAULT NULL COMMENT '接收时间',
  `status` varchar(20) DEFAULT NULL COMMENT '状态',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

