-- Records of statements
-- ----------------------------

-- ----------------------------
-- Table structure for users
-- ----------------------------
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `userId` int(11) NOT NULL AUTO_INCREMENT,
  `address` varchar(255) DEFAULT NULL,
  `areaCode` varchar(255) DEFAULT NULL,
  `checkPwd` varchar(255) DEFAULT NULL,
  `delFlag` varchar(255) DEFAULT NULL,
  `email` varchar(255) DEFAULT NULL,
  `mobileNum` varchar(255) DEFAULT NULL,
  `newPwd` varchar(255) DEFAULT NULL,
  `orderMail` varchar(255) DEFAULT NULL,
  `phoneToken` varchar(255) DEFAULT NULL,
  `photoPath` varchar(255) DEFAULT NULL,
  `position` varchar(255) DEFAULT NULL,
  `pwd` varchar(255) DEFAULT NULL,
  `qrCodePath` varchar(255) DEFAULT NULL,
  `realName` varchar(255) DEFAULT NULL,
  `reportMail` varchar(255) DEFAULT NULL,
  `statementMail` varchar(255) DEFAULT NULL,
  `tel` varchar(255) DEFAULT NULL,
  `updateTime` varchar(255) DEFAULT NULL,
  `userIp` varchar(255) DEFAULT NULL,
  `userName` varchar(255) DEFAULT NULL,
  `website` varchar(255) DEFAULT NULL,
  `groupId` int(11) NOT NULL,
  `roleId` int(11) NOT NULL,
  PRIMARY KEY (`userId`),
  KEY `FK_phyv9hlxu3301xmxqu8m6ntig` (`groupId`),
  KEY `FK_rin8hpeky1j0qj4mwj1lcc6mb` (`roleId`),
  CONSTRAINT `users_ibfk_1` FOREIGN KEY (`groupId`) REFERENCES `groups` (`groupId`),
  CONSTRAINT `users_ibfk_2` FOREIGN KEY (`roleId`) REFERENCES `roles` (`roleId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

